import { Link } from "react-router-dom";

const Login = (props) => {
  const user = props.user;
  let element;
  if (user) {
    element=(<div><p>Hello, {user.name}. Click the button below to view credential repository.</p><br/>
    <button>CLICK</button></div>)
  }
  const onSubmit = (e) => {
    // on submit of form call addJob function passing info of job as input
      e.preventDefault();
      const jwt = e.target[0].value
      props.submit(jwt);
    }
  return (
    <div>
       <Link to='/'><button>HOME</button></Link>
      <form onSubmit={onSubmit}>
          <label htmlFor='Jwt'>Please enter JWT:</label>
          <input type='text' placeholder=''/><br/>
          <input className='btn'type='submit'/>
        </form>
    </div>
  )
}

export default Login
