const HandleUser = (props) => {
    const currUser = props.user;
    let element;
    // Send newly updated info of job through updateJob function
    // Get job info of job with submitted ID
    const getUser = (e) => {
        e.preventDefault();
        props.findUser(e.target[0].value);
    }
    const onSubmit = (e) => {
        e.preventDefault();
        const description = e.target[0].value
        const location = e.target[1].value
        const priority = e.target[2].value
        const status = e.target[3].value.toLowerCase();
        props.updateJob(description, location, priority, status, currJob._id);
      }

      /* If value of currJob is false which is default value, no job has been fetched yet so we display a form that asks for the
      desired job's ID*/
    if (!currUser) {
        element = (
          // On submit of form, trigger findJob function which fetches the info for the job with the submitted ID
        <form onSubmit={getUser}>
          <label htmlFor='user-to-update'>ID of user:</label>
          <input type='text' placeholder='eg. 623db3ffef12f3358885e536'/><br/>
          <input className='btn'type='submit'/>
        </form>
        )
    }
    /* Else if we have job info, display a form that shows the job's info and allows the user to update the info, then onSubmit
    trigger 'onSubmit' function*/
    else {
        element = (
        <form onSubmit={onSubmit}>
            <h1>Change the field(s) you'd like to update then hit submit:</h1>
          <label htmlFor='Surname' >Surname:</label>
          <input type='text' defaultValue={currUser.surname} placeholder='eg. Musk'/><br/>
          <label htmlFor='Location'>Location:</label>
          <input type='text' defaultValue={currJob.location} placeholder='eg. 2A'/><br/>
          <label htmlFor='Priority'>Priority:</label>
          <input type='text' defaultValue={currJob.priority} placeholder='eg. High'/><br/>
          <label htmlFor='Status'>Status:</label>
          <input type='text' defaultValue={currJob.status} placeholder='eg. In progress'/><br/>
          <input className='btn'type='submit'/>
        </form>
        )
    }
  return (
    <div>

    </div>
  )
}

export default HandleUser
